import React, { useState } from 'react';
import { fetchApplicants } from '../api';

export default function AdminView(){
    const [adminKey, setAdminKey] = useState('');
    const [applicants, setApplicants] = useState(null);
    const [error, setError] = useState(null);
async function load() {
  console.log('Using adminKey:', adminKey);  // Check what key is sent
  setError(null);
  setApplicants(null);
  try {
    const res = await fetchApplicants(adminKey);
    if (res.error) setError(res.error);
    else setApplicants(res.applicants);
  } catch {
    setError('Network error');
  }
}

return (
    <div className="card">
        <h2>Admin â€” Applicants</h2>
        <p>Enter admin key to load applicants.</p>
        <div style={{ display: 'flex', gap: 8 }}>
            <input placeholder="admin key" value={adminKey} onChange={e=>setAdminKey(e.target.value)} />
            <button className="btn" onClick={load}>Load</button>
        </div>
    
    {error && <div className="status error">{error}</div>}
    
    {applicants && (
        <div className="table">
            <table>
                <thead>
                    <tr>   
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Role</th>
                        <th>Resume</th>
                        <th>Message</th>
                        <th>When</th>
                    </tr>
                </thead>
                <tbody>
                    {applicants.map(a => (
                        <tr key={a._id}>
                            <td>{a.name}</td>
                            <td>{a.email}</td>
                            <td>{a.phone}</td>
                            <td>{a.role}</td>
                            <td>{a.resumeLink ? <a href={a.resumeLink} target="_blank" rel="noreferrer">link</a> : '-'}</td>
                            <td>{a.message || '-'}</td>
                            <td>{new Date(a.createdAt).toLocaleString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
        )}
        </div>
    );
}
