import React, { useState } from 'react';
import { submitApplicant } from '../api';

export default function RegisterForm(){
    const [form, setForm] = useState({ name: '', email: '', phone: '', role: 'intern', resumeLink: '', message: '' });
    const [status, setStatus] = useState(null);
    function handleChange(e){
        setForm({ ...form, [e.target.name]: e.target.value });
    }
    async function handleSubmit(e){
    e.preventDefault();
    setStatus('submitting');
    try{
        const res = await submitApplicant(form);
        if (res.error) setStatus({ type: 'error', message: res.error });
        else setStatus({ type: 'success', message: 'Application submitted.Thank you!' });
        setForm({ name: '', email: '', phone: '', role: 'intern', resumeLink: '', message: '' });
    } catch (err) {
        setStatus({ type: 'error', message: 'Network error' });
    }
}
return (
    <div className="card">
        <h2>Intern / Volunteer Registration</h2>
        <form onSubmit={handleSubmit} className="form">
            <label>Name <input name="name" value={form.name} onChange={handleChange} required /></label>
            <label>Email <input name="email" value={form.email} onChange={handleChange} type="email" required /></label>
            <label>Phone <input name="phone" value={form.phone} onChange={handleChange} /></label>
            <label>Role
                <select name="role" value={form.role} onChange={handleChange}>
                    <option value="intern">Intern</option>
                    <option value="volunteer">Volunteer</option>
                </select>
            </label>
            <label>Resume link (optional) <input name="resumeLink"
            value={form.resumeLink} onChange={handleChange} /></label>
            <label>Message <textarea name="message" value={form.message}
            onChange={handleChange} /></label>
            <button className="btn" type="submit">Submit</button>
        </form>
        {status && (
            <div className={`status ${status.type ?? ''}`}>
                {status.message ?? (status === 'submitting' ? 'Submitting...' : '')}
            </div>
            )}
        </div>
    );
}
